// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SplenexLiFiWrapper
 * @dev Smart contract that wraps LI.FI swaps and automatically extracts fees
 * @notice Takes fees from input tokens before forwarding to LI.FI
 * 
 * Flow:
 * 1. User transfers input tokens to this contract
 * 2. Contract extracts fee from input tokens and sends to revenue wallet
 * 3. Contract calls LI.FI with remaining tokens (LI.FI sends output to user directly)
 * 4. Contract emits event for tracking
 * 
 * This approach takes fees upfront, then lets LI.FI handle the swap and send tokens to user.
 */
contract SplenexLiFiWrapper is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Revenue wallet for collecting fees
    address public immutable revenueWallet;

    // Fee percentage in basis points (100 = 1%, 10000 = 100%)
    uint256 public feeBps;

    // Maximum fee allowed (500 = 5%)
    uint256 public constant MAX_FEE_BPS = 500;

    // Events
    event SwapExecuted(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 totalFee
    );

    event FeeUpdated(uint256 oldFee, uint256 newFee);

    // Errors
    error InvalidAmount();
    error SwapFailed();
    error TransferFailed();
    error InvalidFee();
    error InsufficientOutput();
    error InvalidLiFiCalldata();

    /**
     * @dev Constructor
     * @param _revenueWallet Address to receive collected fees
     * @param _initialFeeBps Initial fee in basis points (default 200 = 2%)
     */
    constructor(
        address _revenueWallet,
        uint256 _initialFeeBps
    ) {
        if (_revenueWallet == address(0)) revert TransferFailed();
        if (_initialFeeBps > MAX_FEE_BPS) revert InvalidFee();

        revenueWallet = _revenueWallet;
        feeBps = _initialFeeBps;
    }

    /**
     * @dev Execute swap with fee collection in ONE transaction
     * Flow: User → Wrapper (collects 2% fee) → Wrapper calls LI.FI internally → LI.FI swaps
     * 
     * This uses a multicall approach where the wrapper collects the fee first,
     * then the user's wallet executes LI.FI with the remaining tokens.
     */
    function swapTokenViaLiFi(
        address tokenIn,
        uint256 amountIn,
        address tokenOut,
        address lifiContract,
        bytes calldata lifiCalldata,
        uint256 /* minAmountOut */
    ) external nonReentrant returns (uint256 amountOut) {
        if (amountIn == 0) revert InvalidAmount();
        if (lifiContract == address(0)) revert TransferFailed();

        // Step 1: Take all tokens from user
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Step 2: Calculate and send fee to revenue wallet (2%)
        uint256 inputFee = (amountIn * feeBps) / 10000;
        uint256 amountForSwap = amountIn - inputFee;

        if (inputFee > 0) {
            IERC20(tokenIn).safeTransfer(revenueWallet, inputFee);
        }

        // Step 3: Execute LI.FI swap with the remaining tokens
        // Approve LI.FI to pull tokens from this contract (wrapper)
        IERC20(tokenIn).safeApprove(lifiContract, 0); // Reset
        IERC20(tokenIn).safeApprove(lifiContract, amountForSwap);

        // Call LI.FI - this will pull tokens from the wrapper and swap them
        (bool success, bytes memory returnData) = lifiContract.call(lifiCalldata);

        if (!success) {
            // Decode and forward LI.FI error
            if (returnData.length > 0) {
                assembly {
                    let returndata_size := mload(returnData)
                    revert(add(32, returnData), returndata_size)
                }
            }
            revert SwapFailed();
        }

        // Transfer any remaining tokens back to user (should be minimal)
        uint256 remaining = IERC20(tokenIn).balanceOf(address(this));
        if (remaining > 0) {
            IERC20(tokenIn).safeTransfer(msg.sender, remaining);
        }

        emit SwapExecuted(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            amountForSwap,
            inputFee
        );

        return amountForSwap;
    }

    /**
     * @dev Collect fee from native tokens (BNB/ETH)
     * Returns remaining amount to user for them to execute LI.FI swap
     */
    function swapNativeViaLiFi(
        address tokenOut,
        address lifiContract,
        bytes calldata lifiCalldata,
        uint256 /* minAmountOut */
    ) external payable nonReentrant returns (uint256 amountOut) {
        if (msg.value == 0) revert InvalidAmount();
        if (lifiContract == address(0)) revert TransferFailed();

        // Calculate fee (2%)
        uint256 inputFee = (msg.value * feeBps) / 10000;
        uint256 amountForSwap = msg.value - inputFee;

        // Send fee to revenue wallet
        if (inputFee > 0) {
            (bool feeSuccess, ) = revenueWallet.call{value: inputFee}("");
            if (!feeSuccess) revert TransferFailed();
        }

        // Execute LI.FI swap with remaining native amount
        (bool success, bytes memory returnData) = lifiContract.call{value: amountForSwap}(lifiCalldata);

        if (!success) {
            if (returnData.length > 0) {
                assembly {
                    let returndata_size := mload(returnData)
                    revert(add(32, returnData), returndata_size)
                }
            }
            revert SwapFailed();
        }

        // Transfer any remaining native back to user
        uint256 remaining = address(this).balance;
        if (remaining > 0) {
            (bool refundSuccess, ) = msg.sender.call{value: remaining}("");
            if (!refundSuccess) revert TransferFailed();
        }

        amountOut = amountForSwap;

        emit SwapExecuted(
            msg.sender,
            address(0), // native
            tokenOut,
            msg.value,
            amountOut,
            inputFee
        );

        return amountOut;
    }

    /**
     * @dev Update fee percentage (owner only)
     * @param newFeeBps New fee in basis points
     */
    function setFeeBps(uint256 newFeeBps) external onlyOwner {
        if (newFeeBps > MAX_FEE_BPS) revert InvalidFee();

        uint256 oldFee = feeBps;
        feeBps = newFeeBps;

        emit FeeUpdated(oldFee, newFeeBps);
    }

    /**
     * @dev Calculate total fees collected for a given swap
     * @param amountIn Input amount
     * @param expectedOutput Expected output amount
     * @return totalFee Total fee amount
     */
    function calculateTotalFee(
        uint256 amountIn,
        uint256 expectedOutput
    ) external view returns (uint256 totalFee) {
        uint256 inputFee = (amountIn * feeBps) / 10000;
        uint256 outputFee = (expectedOutput * feeBps) / 10000;
        return inputFee + outputFee;
    }

    /**
     * @dev Emergency withdraw tokens (owner only)
     * @param token Token address to withdraw (address(0) for native)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            (bool success, ) = owner().call{value: amount}("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(token).safeTransfer(owner(), amount);
        }
    }

    // Allow contract to receive native tokens
    receive() external payable {}
}

