        if (!lifiSuccess) revert SwapFailed();

        // Send tax to revenue wallet
        (bool taxSuccess, ) = revenueWallet.call{value: taxAmount}("");
        if (!taxSuccess) revert TransferFailed();

        // Refund any excess
        uint256 excess = msg.value - expectedTotal;
        if (excess > 0) {
            (bool refundSuccess, ) = msg.sender.call{value: excess}("");
            if (!refundSuccess) revert TransferFailed();
        }

        emit SwapExecuted(
            msg.sender,
            tokenIn,
            toToken,
            amountIn,
            taxAmount,
            0,
            "lifi"
        );
    }

    /**
     * @dev Handle ERC-2612 permit for token approval
     * @param permitData The permit data including signature
     */
    function _handlePermit(PermitData calldata permitData) internal {
        if (permitData.token == address(0)) return;

        // Call permit function on token contract
        (bool success, bytes memory returnData) = permitData.token.call(
            abi.encodeWithSelector(
                0xd505accf, // permit(address,address,uint256,uint256,uint8,bytes32,bytes32)
                msg.sender,           // owner
                address(this),        // spender
                permitData.value,     // value
                permitData.deadline,  // deadline
                permitData.v,         // v
                permitData.r,         // r
                permitData.s          // s
            )
        );

        if (!success) {
            // If permit fails, revert with the return data
            assembly {
                let returnDataSize := mload(returnData)
                revert(add(32, returnData), returnDataSize)
            }
        }
    }

    // Allow contract to receive ETH
    receive() external payable {}
}

// Struct for ERC-2612 permit data
struct PermitData {
    address token;
    uint256 value;
    uint256 deadline;
    uint8 v;
    bytes32 r;
    bytes32 s;
}

contracts/SplenexDEXAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SplenexDEXAggregator
 * @dev Smart contract that aggregates different DEX routes (LiFi, 1inch, 0x, PancakeSwap, etc.)
 * @notice Charges 0.5% fee on swap amounts and sends to revenue wallet
 */
contract SplenexDEXAggregator is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Revenue wallet address
    address public immutable revenueWallet;

    // Fee rate: 0.5% = 50 basis points
    uint256 public constant FEE_RATE_BPS = 50;
    uint256 public constant BASIS_POINTS = 10000;

    // DEX Router addresses for different chains
    mapping(uint256 => mapping(string => address)) public dexRouters;

    // Supported DEX names
    string[] public supportedDEXes;

    // Route types enum
    enum RouteType {
        LIFI,
        ONE_INCH,
        ZERO_X,
        PANCAKE_SWAP,
        UNISWAP_V2,
        UNISWAP_V3,
        SUSHISWAP,
        QUICKSWAP,
        TRADER_JOE,
        SPIRIT_SWAP,
        SPOOKY_SWAP,
        APE_SWAP,
        BISWAP,
        MDEX
    }

    // Events
    event SwapExecuted(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 feeAmount,
        uint256 amountOut,
        RouteType routeType,
        string dexName
    );

    event DEXRouterUpdated(uint256 chainId, string dexName, address router);
    event RouteSelected(RouteType routeType, string dexName, uint256 expectedOutput);

    // Errors
    error InvalidTokenAddress();
    error InvalidAmount();
    error SwapFailed();
    error InsufficientBalance();
    error TransferFailed();
    error UnsupportedDEX();
    error InvalidRouter();
    error InvalidRouteType();
    error InsufficientOutput();

    constructor(address _revenueWallet) {
        if (_revenueWallet == address(0)) revert InvalidTokenAddress();

        revenueWallet = _revenueWallet;

        // Initialize supported DEXes
        supportedDEXes = [
            "lifi",
            "1inch",
            "0x",
            "pancakeSwapV2",
            "uniswapV2",
            "uniswapV3",
            "sushiswap",
            "quickswap",
            "traderjoe",
            "spiritswap",
            "spookyswap",
            "apeswap",
            "biswap",
            "mdex"
        ];
    }

    /**
     * @dev Execute a token swap with automatic route selection and 0.5% fee
     * @param tokenIn Address of the input token
     * @param tokenOut Address of the output token  
     * @param amountIn Amount of input tokens to swap
     * @param minAmountOut Minimum amount of output tokens expected
     * @param routeTypes Array of preferred route types in order of preference
     * @param swapData Array of encoded swap data for each route type
     */
    function swapWithRouteSelection(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType[] calldata routeTypes,
        bytes[] calldata swapData
    ) external payable nonReentrant {
        if (tokenIn == address(0) || tokenOut == address(0)) revert InvalidTokenAddress();
        if (amountIn == 0) revert InvalidAmount();
        if (routeTypes.length != swapData.length) revert InvalidAmount();

        // Transfer tokens from user to this contract
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Calculate fee (0.5% of swap amount)
        uint256 feeAmount = (amountIn * FEE_RATE_BPS) / BASIS_POINTS;
        uint256 swapAmount = amountIn - feeAmount;

        // Send fee to revenue wallet
        if (feeAmount > 0) {
            if (tokenIn == address(0)) {
                // ETH fee
                (bool success, ) = revenueWallet.call{value: feeAmount}("");
                if (!success) revert TransferFailed();
            } else {
                // Token fee
                IERC20(tokenIn).safeTransfer(revenueWallet, feeAmount);
            }
        }

        // Try each route in order of preference
        uint256 bestAmountOut = 0;
        RouteType bestRoute = routeTypes[0];
        string memory bestDexName = "";

        for (uint256 i = 0; i < routeTypes.length; i++) {
            try this._executeSwapWithRouteInternal(
                tokenIn,
                tokenOut,
                swapAmount,
                minAmountOut,
                routeTypes[i],
                swapData[i]
            ) returns (uint256 amountOut) {
                if (amountOut > bestAmountOut) {
                    bestAmountOut = amountOut;
                    bestRoute = routeTypes[i];
                    bestDexName = _getDexName(routeTypes[i]);
                }
            } catch {
                // Continue to next route if this one fails
                continue;
            }
        }

        if (bestAmountOut == 0) revert SwapFailed();
        if (bestAmountOut < minAmountOut) revert InsufficientOutput();

        // Transfer output tokens to user
        IERC20(tokenOut).safeTransfer(msg.sender, bestAmountOut);

        emit SwapExecuted(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            feeAmount,
            bestAmountOut,
            bestRoute,
            bestDexName
        );

        emit RouteSelected(bestRoute, bestDexName, bestAmountOut);
    }

    /**
     * @dev Execute ETH swap with automatic route selection and 0.5% fee
     * @param tokenOut Address of the output token
     * @param minAmountOut Minimum amount of output tokens expected
     * @param routeTypes Array of preferred route types in order of preference
     * @param swapData Array of encoded swap data for each route type
     */
    function swapETHWithRouteSelection(
        address tokenOut,
        uint256 minAmountOut,
        RouteType[] calldata routeTypes,
        bytes[] calldata swapData
    ) external payable nonReentrant {
        if (tokenOut == address(0)) revert InvalidTokenAddress();
        if (msg.value == 0) revert InvalidAmount();
        if (routeTypes.length != swapData.length) revert InvalidAmount();

        // Calculate fee (0.5% of swap amount)
        uint256 feeAmount = (msg.value * FEE_RATE_BPS) / BASIS_POINTS;
        uint256 swapAmount = msg.value - feeAmount;

        // Send fee to revenue wallet
        if (feeAmount > 0) {
            (bool success, ) = revenueWallet.call{value: feeAmount}("");
            if (!success) revert TransferFailed();
        }

        // Try each route in order of preference
        uint256 bestAmountOut = 0;
        RouteType bestRoute = routeTypes[0];
        string memory bestDexName = "";

        for (uint256 i = 0; i < routeTypes.length; i++) {
            try this._executeSwapETHWithRouteInternal(
                tokenOut,
                swapAmount,
                minAmountOut,
                routeTypes[i],
                swapData[i]
            ) returns (uint256 amountOut) {
                if (amountOut > bestAmountOut) {
                    bestAmountOut = amountOut;
                    bestRoute = routeTypes[i];
                    bestDexName = _getDexName(routeTypes[i]);
                }
            } catch {
                // Continue to next route if this one fails
                continue;
            }
        }

        if (bestAmountOut == 0) revert SwapFailed();
        if (bestAmountOut < minAmountOut) revert InsufficientOutput();

        // Transfer output tokens to user
        IERC20(tokenOut).safeTransfer(msg.sender, bestAmountOut);

        emit SwapExecuted(
            msg.sender,
            address(0),
            tokenOut,
            msg.value,
            feeAmount,
            bestAmountOut,
            bestRoute,
            bestDexName
        );

        emit RouteSelected(bestRoute, bestDexName, bestAmountOut);
    }

    /**
     * @dev Execute token to ETH swap with automatic route selection and 0.5% fee
     * @param tokenIn Address of the input token
     * @param amountIn Amount of input tokens to swap
     * @param minAmountOut Minimum amount of ETH expected
     * @param routeTypes Array of preferred route types in order of preference
     * @param swapData Array of encoded swap data for each route type
     */
    function swapToETHWithRouteSelection(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType[] calldata routeTypes,
        bytes[] calldata swapData
    ) external payable nonReentrant {
        if (tokenIn == address(0)) revert InvalidTokenAddress();
        if (amountIn == 0) revert InvalidAmount();
        if (routeTypes.length != swapData.length) revert InvalidAmount();

        // Transfer tokens from user to this contract
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Calculate fee (0.5% of swap amount)
        uint256 feeAmount = (amountIn * FEE_RATE_BPS) / BASIS_POINTS;
        uint256 swapAmount = amountIn - feeAmount;

        // Send fee to revenue wallet
        if (feeAmount > 0) {
            IERC20(tokenIn).safeTransfer(revenueWallet, feeAmount);
        }

        // Try each route in order of preference
        uint256 bestAmountOut = 0;
        RouteType bestRoute = routeTypes[0];
        string memory bestDexName = "";

        for (uint256 i = 0; i < routeTypes.length; i++) {
            try this._executeSwapToETHWithRouteInternal(
                tokenIn,
                swapAmount,
                minAmountOut,
                routeTypes[i],
                swapData[i]
            ) returns (uint256 amountOut) {
                if (amountOut > bestAmountOut) {
                    bestAmountOut = amountOut;
                    bestRoute = routeTypes[i];
                    bestDexName = _getDexName(routeTypes[i]);
                }
            } catch {
                // Continue to next route if this one fails
                continue;
            }
        }

        if (bestAmountOut == 0) revert SwapFailed();
        if (bestAmountOut < minAmountOut) revert InsufficientOutput();

        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: bestAmountOut}("");
        if (!success) revert TransferFailed();

        emit SwapExecuted(
            msg.sender,
            tokenIn,
            address(0),
            amountIn,
            feeAmount,
            bestAmountOut,
            bestRoute,
            bestDexName
        );

        emit RouteSelected(bestRoute, bestDexName, bestAmountOut);
    }

    /**
     * @dev Execute swap with specific route type
     * @param tokenIn Address of the input token
     * @param tokenOut Address of the output token  
     * @param amountIn Amount of input tokens to swap
     * @param minAmountOut Minimum amount of output tokens expected
     * @param routeType Specific route type to use
     * @param swapData Encoded swap data for the route
     */
    function swapWithSpecificRoute(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) external payable nonReentrant {
        if (tokenIn == address(0) || tokenOut == address(0)) revert InvalidTokenAddress();
        if (amountIn == 0) revert InvalidAmount();

        // Transfer tokens from user to this contract
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Calculate fee (0.5% of swap amount)
        uint256 feeAmount = (amountIn * FEE_RATE_BPS) / BASIS_POINTS;
        uint256 swapAmount = amountIn - feeAmount;

        // Send fee to revenue wallet
        if (feeAmount > 0) {
            IERC20(tokenIn).safeTransfer(revenueWallet, feeAmount);
        }

        // Execute the swap
        uint256 amountOut = _executeSwapWithRoute(
            tokenIn,
            tokenOut,
            swapAmount,
            minAmountOut,
            routeType,
            swapData
        );

        // Transfer output tokens to user
        IERC20(tokenOut).safeTransfer(msg.sender, amountOut);

        emit SwapExecuted(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            feeAmount,
            amountOut,
            routeType,
            _getDexName(routeType)
        );
    }

    /**
     * @dev External function to execute token-to-token swap with specific route (for try-catch)
     */
    function _executeSwapWithRouteInternal(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) external returns (uint256 amountOut) {
        return _executeSwapWithRoute(tokenIn, tokenOut, amountIn, minAmountOut, routeType, swapData);
    }

    /**
     * @dev External function to execute ETH-to-token swap with specific route (for try-catch)
     */
    function _executeSwapETHWithRouteInternal(
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) external returns (uint256 amountOut) {
        return _executeSwapETHWithRoute(tokenOut, amountIn, minAmountOut, routeType, swapData);
    }

    /**
     * @dev External function to execute token-to-ETH swap with specific route (for try-catch)
     */
    function _executeSwapToETHWithRouteInternal(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) external returns (uint256 amountOut) {
        return _executeSwapToETHWithRoute(tokenIn, amountIn, minAmountOut, routeType, swapData);
    }

    /**
     * @dev Internal function to execute token-to-token swap with specific route
     */
    function _executeSwapWithRoute(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        if (routeType == RouteType.LIFI) {
            return _executeLiFiSwap(tokenIn, tokenOut, amountIn, minAmountOut, swapData);
        } else if (routeType == RouteType.ONE_INCH) {
            return _executeOneInchSwap(tokenIn, tokenOut, amountIn, minAmountOut, swapData);
        } else if (routeType == RouteType.ZERO_X) {
            return _executeZeroXSwap(tokenIn, tokenOut, amountIn, minAmountOut, swapData);
        } else {
            return _executeDEXSwap(tokenIn, tokenOut, amountIn, minAmountOut, routeType, swapData);
        }
    }

    /**
     * @dev Internal function to execute ETH-to-token swap with specific route
     */
    function _executeSwapETHWithRoute(
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        if (routeType == RouteType.LIFI) {
            return _executeLiFiSwapETH(tokenOut, amountIn, minAmountOut, swapData);
        } else if (routeType == RouteType.ONE_INCH) {
            return _executeOneInchSwapETH(tokenOut, amountIn, minAmountOut, swapData);
        } else if (routeType == RouteType.ZERO_X) {
            return _executeZeroXSwapETH(tokenOut, amountIn, minAmountOut, swapData);
        } else {
            return _executeDEXSwapETH(tokenOut, amountIn, minAmountOut, routeType, swapData);
        }
    }

    /**
     * @dev Internal function to execute token-to-ETH swap with specific route
     */
    function _executeSwapToETHWithRoute(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        if (routeType == RouteType.LIFI) {
            return _executeLiFiSwapToETH(tokenIn, amountIn, minAmountOut, swapData);
        } else if (routeType == RouteType.ONE_INCH) {
            return _executeOneInchSwapToETH(tokenIn, amountIn, minAmountOut, swapData);
        } else if (routeType == RouteType.ZERO_X) {
            return _executeZeroXSwapToETH(tokenIn, amountIn, minAmountOut, swapData);
        } else {
            return _executeDEXSwapToETH(tokenIn, amountIn, minAmountOut, routeType, swapData);
        }
    }

    /**
     * @dev Execute LiFi swap
     */
    function _executeLiFiSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Approve LiFi to spend tokens
        IERC20(tokenIn).safeApprove(address(this), amountIn);

        // Execute swap via LiFi
        (bool success, bytes memory returnData) = address(this).call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute LiFi ETH swap
     */
    function _executeLiFiSwapETH(
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Execute swap via LiFi
        (bool success, bytes memory returnData) = address(this).call{value: amountIn}(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute LiFi token to ETH swap
     */
    function _executeLiFiSwapToETH(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Approve LiFi to spend tokens
        IERC20(tokenIn).safeApprove(address(this), amountIn);

        // Execute swap via LiFi
        (bool success, bytes memory returnData) = address(this).call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute 1inch swap
     */
    function _executeOneInchSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Get 1inch router address
        address router = dexRouters[block.chainid]["1inch"];
        if (router == address(0)) revert UnsupportedDEX();

        // Approve 1inch router to spend tokens
        IERC20(tokenIn).safeApprove(router, amountIn);

        // Execute swap via 1inch
        (bool success, bytes memory returnData) = router.call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute 1inch ETH swap
     */
    function _executeOneInchSwapETH(
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Get 1inch router address
        address router = dexRouters[block.chainid]["1inch"];
        if (router == address(0)) revert UnsupportedDEX();

        // Execute swap via 1inch
        (bool success, bytes memory returnData) = router.call{value: amountIn}(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute 1inch token to ETH swap
     */
    function _executeOneInchSwapToETH(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Get 1inch router address
        address router = dexRouters[block.chainid]["1inch"];
        if (router == address(0)) revert UnsupportedDEX();

        // Approve 1inch router to spend tokens
        IERC20(tokenIn).safeApprove(router, amountIn);

        // Execute swap via 1inch
        (bool success, bytes memory returnData) = router.call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute 0x swap
     */
    function _executeZeroXSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Get 0x router address
        address router = dexRouters[block.chainid]["0x"];
        if (router == address(0)) revert UnsupportedDEX();

        // Approve 0x router to spend tokens
        IERC20(tokenIn).safeApprove(router, amountIn);

        // Execute swap via 0x
        (bool success, bytes memory returnData) = router.call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute 0x ETH swap
     */
    function _executeZeroXSwapETH(
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Get 0x router address
        address router = dexRouters[block.chainid]["0x"];
        if (router == address(0)) revert UnsupportedDEX();

        // Execute swap via 0x
        (bool success, bytes memory returnData) = router.call{value: amountIn}(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute 0x token to ETH swap
     */
    function _executeZeroXSwapToETH(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        // Get 0x router address
        address router = dexRouters[block.chainid]["0x"];
        if (router == address(0)) revert UnsupportedDEX();

        // Approve 0x router to spend tokens
        IERC20(tokenIn).safeApprove(router, amountIn);

        // Execute swap via 0x
        (bool success, bytes memory returnData) = router.call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute DEX swap (PancakeSwap, Uniswap, etc.)
     */
    function _executeDEXSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        string memory dexName = _getDexName(routeType);
        address router = dexRouters[block.chainid][dexName];
        if (router == address(0)) revert UnsupportedDEX();

        // Approve DEX router to spend tokens
        IERC20(tokenIn).safeApprove(router, amountIn);

        // Execute swap via DEX router
        (bool success, bytes memory returnData) = router.call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute DEX ETH swap
     */
    function _executeDEXSwapETH(
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        string memory dexName = _getDexName(routeType);
        address router = dexRouters[block.chainid][dexName];
        if (router == address(0)) revert UnsupportedDEX();

        // Execute swap via DEX router
        (bool success, bytes memory returnData) = router.call{value: amountIn}(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Execute DEX token to ETH swap
     */
    function _executeDEXSwapToETH(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut,
        RouteType routeType,
        bytes calldata swapData
    ) internal returns (uint256 amountOut) {
        string memory dexName = _getDexName(routeType);
        address router = dexRouters[block.chainid][dexName];
        if (router == address(0)) revert UnsupportedDEX();

        // Approve DEX router to spend tokens
        IERC20(tokenIn).safeApprove(router, amountIn);

        // Execute swap via DEX router
        (bool success, bytes memory returnData) = router.call(swapData);
        if (!success) revert SwapFailed();

        amountOut = abi.decode(returnData, (uint256));
        if (amountOut < minAmountOut) revert SwapFailed();

        return amountOut;
    }

    /**
     * @dev Get DEX name from route type
     */
    function _getDexName(RouteType routeType) internal pure returns (string memory) {
        if (routeType == RouteType.LIFI) return "lifi";
        if (routeType == RouteType.ONE_INCH) return "1inch";
        if (routeType == RouteType.ZERO_X) return "0x";
        if (routeType == RouteType.PANCAKE_SWAP) return "pancakeSwapV2";
        if (routeType == RouteType.UNISWAP_V2) return "uniswapV2";
        if (routeType == RouteType.UNISWAP_V3) return "uniswapV3";
        if (routeType == RouteType.SUSHISWAP) return "sushiswap";
        if (routeType == RouteType.QUICKSWAP) return "quickswap";
        if (routeType == RouteType.TRADER_JOE) return "traderjoe";
        if (routeType == RouteType.SPIRIT_SWAP) return "spiritswap";
        if (routeType == RouteType.SPOOKY_SWAP) return "spookyswap";
        if (routeType == RouteType.APE_SWAP) return "apeswap";
        if (routeType == RouteType.BISWAP) return "biswap";
        if (routeType == RouteType.MDEX) return "mdex";

        revert InvalidRouteType();
    }

    /**
     * @dev Add or update DEX router address (owner only)
     * @param chainId Chain ID
     * @param dexName Name of the DEX
     * @param routerAddress Router contract address
     */
    function updateDEXRouter(
        uint256 chainId,
        string calldata dexName,
        address routerAddress
    ) external onlyOwner {
        if (routerAddress == address(0)) revert InvalidRouter();

        dexRouters[chainId][dexName] = routerAddress;

        emit DEXRouterUpdated(chainId, dexName, routerAddress);
    }

    /**
     * @dev Emergency function to withdraw stuck tokens (owner only)
     * @param token Address of the token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            // Withdraw ETH
            (bool success, ) = owner().call{value: amount}("");
            if (!success) revert TransferFailed();
        } else {
            // Withdraw ERC20 tokens
            IERC20(token).safeTransfer(owner(), amount);
        }
    }

    /**
     * @dev Calculate fee amount for a given swap amount
     * @param swapAmount Amount to calculate fee for
     * @return feeAmount Fee amount (0.5%)
     */
    function calculateFee(uint256 swapAmount) external pure returns (uint256 feeAmount) {
        feeAmount = (swapAmount * FEE_RATE_BPS) / BASIS_POINTS;
    }

    /**
     * @dev Get supported DEXes for current chain
     * @return Array of supported DEX names
     */
    function getSupportedDEXes() external view returns (string[] memory) {
        return supportedDEXes;
    }

    /**
     * @dev Check if DEX is supported on current chain
     * @param dexName Name of the DEX
     * @return True if supported
     */
    function isDEXSupported(string calldata dexName) external view returns (bool) {
        return dexRouters[block.chainid][dexName] != address(0);
    }

    /**
     * @dev Get router address for specific DEX on current chain
     * @param dexName Name of the DEX
     * @return Router address
     */
    function getDEXRouter(string calldata dexName) external view returns (address) {
        return dexRouters[block.chainid][dexName];
    }

    /**
     * @dev Get fee rate in basis points
     * @return Fee rate (50 = 0.5%)
     */
    function getFeeRate() external pure returns (uint256) {
        return FEE_RATE_BPS;
    }

    // Allow contract to receive ETH
    receive() external payable {}
}

