     * @param chainId Chain ID
     * @param dexName Name of the DEX
     * @return True if supported
     */
    function isDEXSupported(uint256 chainId, string memory dexName) external view returns (bool) {
        return routerAddresses[chainId][dexName] != address(0);
    }

    /**
     * @dev Get all supported DEXes for a specific chain
     * @param chainId Chain ID
     * @return Array of supported DEX names
     */
    function getSupportedDEXes(uint256 chainId) external view returns (string[] memory) {
        string[] memory dexNames = new string[](14);
        uint256 count = 0;

        // Check each DEX
        if (routerAddresses[chainId][LIFI] != address(0)) {
            dexNames[count] = LIFI;
            count++;
        }
        if (routerAddresses[chainId][ONE_INCH] != address(0)) {
            dexNames[count] = ONE_INCH;
            count++;
        }
        if (routerAddresses[chainId][ZERO_X] != address(0)) {
            dexNames[count] = ZERO_X;
            count++;
        }
        if (routerAddresses[chainId][PANCAKE_SWAP_V2] != address(0)) {
            dexNames[count] = PANCAKE_SWAP_V2;
            count++;
        }
        if (routerAddresses[chainId][UNISWAP_V2] != address(0)) {
            dexNames[count] = UNISWAP_V2;
            count++;
        }
        if (routerAddresses[chainId][UNISWAP_V3] != address(0)) {
            dexNames[count] = UNISWAP_V3;
            count++;
        }
        if (routerAddresses[chainId][SUSHISWAP] != address(0)) {
            dexNames[count] = SUSHISWAP;
            count++;
        }
        if (routerAddresses[chainId][QUICKSWAP] != address(0)) {
            dexNames[count] = QUICKSWAP;
            count++;
        }
        if (routerAddresses[chainId][TRADER_JOE] != address(0)) {
            dexNames[count] = TRADER_JOE;
            count++;
        }
        if (routerAddresses[chainId][SPIRIT_SWAP] != address(0)) {
            dexNames[count] = SPIRIT_SWAP;
            count++;
        }
        if (routerAddresses[chainId][SPOOKY_SWAP] != address(0)) {
            dexNames[count] = SPOOKY_SWAP;
            count++;
        }
        if (routerAddresses[chainId][APE_SWAP] != address(0)) {
            dexNames[count] = APE_SWAP;
            count++;
        }
        if (routerAddresses[chainId][BISWAP] != address(0)) {
            dexNames[count] = BISWAP;
            count++;
        }
        if (routerAddresses[chainId][MDEX] != address(0)) {
            dexNames[count] = MDEX;
            count++;
        }

        // Create result array with correct size
        string[] memory result = new string[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = dexNames[i];
        }

        return result;
    }

    /**
     * @dev Get chain name from chain ID
     * @param chainId Chain ID
     * @return Chain name
     */
    function getChainName(uint256 chainId) external pure returns (string memory) {
        if (chainId == ETHEREUM_MAINNET) return "Ethereum Mainnet";
        if (chainId == BSC_MAINNET) return "BSC Mainnet";
        if (chainId == POLYGON_MAINNET) return "Polygon Mainnet";
        if (chainId == AVALANCHE_MAINNET) return "Avalanche Mainnet";
        if (chainId == FANTOM_MAINNET) return "Fantom Mainnet";
        if (chainId == ARBITRUM_MAINNET) return "Arbitrum Mainnet";
        if (chainId == OPTIMISM_MAINNET) return "Optimism Mainnet";
        if (chainId == BASE_MAINNET) return "Base Mainnet";

        return "Unknown Chain";
    }
}

