// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SplenexLiFiWrapper
 * @dev Simplified smart contract that wraps LI.FI swaps and automatically extracts fees
 * @notice This contract removes all internal routing logic - it simply forwards swaps to LI.FI
 * 
 * IMPORTANT: This contract requires the frontend to modify LI.FI's transaction request
 * to redirect output tokens to this contract address instead of the user.
 * 
 * Flow:
 * 1. User calls contract with input tokens/ETH + modified LI.FI calldata
 * 2. Contract extracts fee from input tokens
 * 3. Contract calls LI.FI with the provided calldata (which sends output to contract)
 * 4. LI.FI performs swap and sends output tokens to this contract
 * 5. Contract extracts fee from output tokens  
 * 6. Contract sends remaining output tokens to user
 * 
 * The frontend must:
 * - Get a quote from LI.FI API
 * - Modify the transaction request to change the recipient to this contract's address
 * - Include the modified calldata when calling this contract
 */
contract SplenexLiFiWrapper is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Revenue wallet for collecting fees
    address public immutable revenueWallet;

    // Fee percentage in basis points (100 = 1%, 10000 = 100%)
    uint256 public feeBps;

    // Maximum fee allowed (500 = 5%)
    uint256 public constant MAX_FEE_BPS = 500;

    // Events
    event SwapExecuted(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 totalFee
    );

    event FeeUpdated(uint256 oldFee, uint256 newFee);

    // Errors
    error InvalidAmount();
    error SwapFailed();
    error TransferFailed();
    error InvalidFee();
    error InsufficientOutput();

    /**
     * @dev Constructor
     * @param _revenueWallet Address to receive collected fees
     * @param _initialFeeBps Initial fee in basis points (default 200 = 2%)
     */
    constructor(
        address _revenueWallet,
        uint256 _initialFeeBps
    ) {
        if (_revenueWallet == address(0)) revert TransferFailed();
        if (_initialFeeBps > MAX_FEE_BPS) revert InvalidFee();

        revenueWallet = _revenueWallet;
        feeBps = _initialFeeBps;
    }

    /**
     * @dev Execute swap via LI.FI for ERC20 tokens
     * @param tokenIn Input token address
     * @param amountIn Amount of input tokens
     * @param tokenOut Output token address
     * @param lifiContract Address of LI.FI contract to call
     * @param lifiCalldata Calldata for LI.FI contract
     * @param minAmountOut Minimum output amount expected
     * @return amountOut Amount of output tokens received
     */
    function swapTokenViaLiFi(
        address tokenIn,
        uint256 amountIn,
        address tokenOut,
        address lifiContract,
        bytes calldata lifiCalldata,
        uint256 minAmountOut
    ) external nonReentrant returns (uint256 amountOut) {
        if (amountIn == 0) revert InvalidAmount();
        if (lifiContract == address(0)) revert TransferFailed();

        // Transfer input tokens from user to this contract
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Extract fee from input tokens
        uint256 inputFee = (amountIn * feeBps) / 10000;
        uint256 amountForSwap = amountIn - inputFee;

        if (inputFee > 0) {
            IERC20(tokenIn).safeTransfer(revenueWallet, inputFee);
        }

        // Approve LI.FI contract to spend tokens
        IERC20(tokenIn).safeApprove(lifiContract, amountForSwap);

        // Record balance before LI.FI call
        uint256 balanceBefore = IERC20(tokenOut).balanceOf(address(this));

        // Call LI.FI contract (modified calldata will redirect output to this contract)
        (bool success, ) = lifiContract.call(
            modifyLiFiCallData(lifiCalldata)
        );
        if (!success) revert SwapFailed();

        // Calculate output amount
        uint256 balanceAfter = IERC20(tokenOut).balanceOf(address(this));
        amountOut = balanceAfter - balanceBefore;

        if (amountOut < minAmountOut) revert InsufficientOutput();

        // Extract fee from output tokens
        uint256 outputFee = (amountOut * feeBps) / 10000;
        uint256 outputForUser = amountOut - outputFee;

        if (outputFee > 0) {
            IERC20(tokenOut).safeTransfer(revenueWallet, outputFee);
        }

        // Transfer remaining output tokens to user
        IERC20(tokenOut).safeTransfer(msg.sender, outputForUser);

        emit SwapExecuted(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            amountOut,
            inputFee + outputFee
        );

        return amountOut;
    }

    /**
     * @dev Execute swap via LI.FI for native token (ETH/BNB) to ERC20
     * @param tokenOut Output token address
     * @param lifiContract Address of LI.FI contract to call
     * @param lifiCalldata Calldata for LI.FI contract
     * @param minAmountOut Minimum output amount expected
     * @return amountOut Amount of output tokens received
     */
    function swapNativeViaLiFi(
        address tokenOut,
        address lifiContract,
        bytes calldata lifiCalldata,
        uint256 minAmountOut
    ) external payable nonReentrant returns (uint256 amountOut) {
        if (msg.value == 0) revert InvalidAmount();
        if (lifiContract == address(0)) revert TransferFailed();

        // Extract fee from native amount
        uint256 inputFee = (msg.value * feeBps) / 10000;
        uint256 amountForSwap = msg.value - inputFee;

        // Send fee to revenue wallet
        if (inputFee > 0) {
            (bool feeSuccess, ) = revenueWallet.call{value: inputFee}("");
            if (!feeSuccess) revert TransferFailed();
        }

        // Record balance before LI.FI call
        uint256 balanceBefore = IERC20(tokenOut).balanceOf(address(this));

        // Call LI.FI with modified calldata and remaining amount
        (bool success, ) = lifiContract.call{value: amountForSwap}(
            modifyLiFiCallData(lifiCalldata)
        );
        if (!success) revert SwapFailed();

        // Calculate output amount
        uint256 balanceAfter = IERC20(tokenOut).balanceOf(address(this));
        amountOut = balanceAfter - balanceBefore;

        if (amountOut < minAmountOut) revert InsufficientOutput();

        // Extract fee from output tokens
        uint256 outputFee = (amountOut * feeBps) / 10000;
        uint256 outputForUser = amountOut - outputFee;

        if (outputFee > 0) {
            IERC20(tokenOut).safeTransfer(revenueWallet, outputFee);
        }

        // Transfer remaining output tokens to user
        IERC20(tokenOut).safeTransfer(msg.sender, outputForUser);

        emit SwapExecuted(
            msg.sender,
            address(0),
            tokenOut,
            msg.value,
            amountOut,
            inputFee + outputFee
        );

        return amountOut;
    }

    /**
     * @dev Modify LI.FI calldata to redirect output tokens to this contract
     * NOTE: Frontend must provide properly formatted calldata that sends tokens to this contract
     * This is a placeholder - actual implementation should parse LI.FI's calldata structure
     */
    function modifyLiFiCallData(bytes calldata originalData) internal pure returns (bytes memory) {
        // For now, just return the original data
        // In production, you need to:
        // 1. Decode LI.FI's calldata structure
        // 2. Find the recipient parameter
        // 3. Replace it with address(this)
        // 4. Re-encode the calldata

        // This requires knowing LI.FI's exact contract interface and ABI
        // The frontend should handle this modification before calling this contract
        return originalData;
    }

    /**
     * @dev Update fee percentage (owner only)
     * @param newFeeBps New fee in basis points
     */
    function setFeeBps(uint256 newFeeBps) external onlyOwner {
        if (newFeeBps > MAX_FEE_BPS) revert InvalidFee();

        uint256 oldFee = feeBps;
        feeBps = newFeeBps;

        emit FeeUpdated(oldFee, newFeeBps);
    }

    /**
     * @dev Calculate total fees collected for a given swap
     * @param amountIn Input amount
     * @param expectedOutput Expected output amount
     * @return totalFee Total fee amount
     */
    function calculateTotalFee(
        uint256 amountIn,
        uint256 expectedOutput
    ) external view returns (uint256 totalFee) {
        uint256 inputFee = (amountIn * feeBps) / 10000;
        uint256 outputFee = (expectedOutput * feeBps) / 10000;
        return inputFee + outputFee;
    }

    /**
     * @dev Emergency withdraw tokens (owner only)
     * @param token Token address to withdraw (address(0) for native)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            (bool success, ) = owner().call{value: amount}("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(token).safeTransfer(owner(), amount);
        }
    }

    // Allow contract to receive native tokens
    receive() external payable {}
}

